// Register Top module auto-generated by `reggen`

module regdemo_reg_top (
  input clk_i,
  input rst_ni,

  // Below Regster interface can be changed
  input  tlul_pkg::tl_h2d_t tl_i,
  output tlul_pkg::tl_d2h_t tl_o,
  // To HW
  output regdemo_reg_pkg::regdemo_reg2hw_t reg2hw, // Write
  input  regdemo_reg_pkg::regdemo_hw2reg_t hw2reg, // Read

  // Config
  input devmode_i // If 1, explicit error return for unmapped register access
);

  import regdemo_reg_pkg::* ;

  localparam int AW = 4;
  localparam int DW = 32;
  localparam int DBW = DW/8;                    // Byte Width

  // register signals
  logic           reg_we;
  logic           reg_re;
  logic [AW-1:0]  reg_addr;
  logic [DW-1:0]  reg_wdata;
  logic [DBW-1:0] reg_be;
  logic [DW-1:0]  reg_rdata;
  logic           reg_error;

  logic          addrmiss, wr_err;

  logic [DW-1:0] reg_rdata_next;

  tlul_pkg::tl_h2d_t tl_reg_h2d;
  tlul_pkg::tl_d2h_t tl_reg_d2h;

  assign tl_reg_h2d = tl_i;
  assign tl_o       = tl_reg_d2h;

  tlul_adapter_reg #(
    .RegAw(AW),
    .RegDw(DW)
  ) u_reg_if (
    .clk_i,
    .rst_ni,

    .tl_i (tl_reg_h2d),
    .tl_o (tl_reg_d2h),

    .we_o    (reg_we),
    .re_o    (reg_re),
    .addr_o  (reg_addr),
    .wdata_o (reg_wdata),
    .be_o    (reg_be),
    .rdata_i (reg_rdata),
    .error_i (reg_error)
  );

  assign reg_rdata = reg_rdata_next ;
  assign reg_error = (devmode_i & addrmiss) | wr_err ;

  // Define SW related signals
  // Format: <reg>_<field>_{wd|we|qs}
  //        or <reg>_{wd|we|qs} if field == 1 or 0
  logic [31:0] shiftout_qs;
  logic shiftout_re;
  logic [31:0] shiftin_qs;
  logic [31:0] shiftin_wd;
  logic shiftin_we;
  logic shiftcfg_dir_qs;
  logic shiftcfg_dir_wd;
  logic shiftcfg_dir_we;
  logic [4:0] shiftcfg_amt_qs;
  logic [4:0] shiftcfg_amt_wd;
  logic shiftcfg_amt_we;

  // Register instances
  // R[shiftout]: V(True)

  prim_subreg_ext #(
    .DW    (32)
  ) u_shiftout (
    .re     (shiftout_re),
    .we     (1'b0),
    .wd     ('0),
    .d      (hw2reg.shiftout.d),
    .qre    (),
    .qe     (),
    .q      (),
    .qs     (shiftout_qs)
  );


  // R[shiftin]: V(False)

  prim_subreg #(
    .DW      (32),
    .SWACCESS("RW"),
    .RESVAL  (32'h0)
  ) u_shiftin (
    .clk_i   (clk_i    ),
    .rst_ni  (rst_ni  ),

    // from register interface
    .we     (shiftin_we),
    .wd     (shiftin_wd),

    // from internal hardware
    .de     (1'b0),
    .d      ('0  ),

    // to internal hardware
    .qe     (),
    .q      (reg2hw.shiftin.q ),

    // to register interface (read)
    .qs     (shiftin_qs)
  );


  // R[shiftcfg]: V(False)

  //   F[dir]: 0:0
  prim_subreg #(
    .DW      (1),
    .SWACCESS("RW"),
    .RESVAL  (1'h0)
  ) u_shiftcfg_dir (
    .clk_i   (clk_i    ),
    .rst_ni  (rst_ni  ),

    // from register interface
    .we     (shiftcfg_dir_we),
    .wd     (shiftcfg_dir_wd),

    // from internal hardware
    .de     (1'b0),
    .d      ('0  ),

    // to internal hardware
    .qe     (),
    .q      (reg2hw.shiftcfg.dir.q ),

    // to register interface (read)
    .qs     (shiftcfg_dir_qs)
  );


  //   F[amt]: 5:1
  prim_subreg #(
    .DW      (5),
    .SWACCESS("RW"),
    .RESVAL  (5'h0)
  ) u_shiftcfg_amt (
    .clk_i   (clk_i    ),
    .rst_ni  (rst_ni  ),

    // from register interface
    .we     (shiftcfg_amt_we),
    .wd     (shiftcfg_amt_wd),

    // from internal hardware
    .de     (1'b0),
    .d      ('0  ),

    // to internal hardware
    .qe     (),
    .q      (reg2hw.shiftcfg.amt.q ),

    // to register interface (read)
    .qs     (shiftcfg_amt_qs)
  );




  logic [2:0] addr_hit;
  always_comb begin
    addr_hit = '0;
    addr_hit[0] = (reg_addr == REGDEMO_SHIFTOUT_OFFSET);
    addr_hit[1] = (reg_addr == REGDEMO_SHIFTIN_OFFSET);
    addr_hit[2] = (reg_addr == REGDEMO_SHIFTCFG_OFFSET);
  end

  assign addrmiss = (reg_re || reg_we) ? ~|addr_hit : 1'b0 ;

  // Check sub-word write is permitted
  always_comb begin
    wr_err = 1'b0;
    if (addr_hit[0] && reg_we && (REGDEMO_PERMIT[0] != (REGDEMO_PERMIT[0] & reg_be))) wr_err = 1'b1 ;
    if (addr_hit[1] && reg_we && (REGDEMO_PERMIT[1] != (REGDEMO_PERMIT[1] & reg_be))) wr_err = 1'b1 ;
    if (addr_hit[2] && reg_we && (REGDEMO_PERMIT[2] != (REGDEMO_PERMIT[2] & reg_be))) wr_err = 1'b1 ;
  end

  assign shiftout_re = addr_hit[0] && reg_re;

  assign shiftin_we = addr_hit[1] & reg_we & ~wr_err;
  assign shiftin_wd = reg_wdata[31:0];

  assign shiftcfg_dir_we = addr_hit[2] & reg_we & ~wr_err;
  assign shiftcfg_dir_wd = reg_wdata[0];

  assign shiftcfg_amt_we = addr_hit[2] & reg_we & ~wr_err;
  assign shiftcfg_amt_wd = reg_wdata[5:1];

  // Read data return
  always_comb begin
    reg_rdata_next = '0;
    unique case (1'b1)
      addr_hit[0]: begin
        reg_rdata_next[31:0] = shiftout_qs;
      end

      addr_hit[1]: begin
        reg_rdata_next[31:0] = shiftin_qs;
      end

      addr_hit[2]: begin
        reg_rdata_next[0] = shiftcfg_dir_qs;
        reg_rdata_next[5:1] = shiftcfg_amt_qs;
      end

      default: begin
        reg_rdata_next = '1;
      end
    endcase
  end

  // Assertions for Register Interface
  `ASSERT_PULSE(wePulse, reg_we, clk_i, !rst_ni)
  `ASSERT_PULSE(rePulse, reg_re, clk_i, !rst_ni)

  `ASSERT(reAfterRv, $rose(reg_re || reg_we) |=> tl_o.d_valid, clk_i, !rst_ni)

  `ASSERT(en2addrHit, (reg_we || reg_re) |-> $onehot0(addr_hit), clk_i, !rst_ni)

  // this is formulated as an assumption such that the FPV testbenches do disprove this
  // property by mistake
  `ASSUME(reqParity, tl_reg_h2d.a_valid |-> tl_reg_h2d.a_user.parity_en == 1'b0, clk_i, !rst_ni)

endmodule
